mapper.select:


     and apply_type = #{applyType}
     and document_type = #{documentType}
     and project_id = #{projectId}
     and project_name like concat('%', #{projectName}, '%')
     and company_name like concat('%', #{companyName}, '%')
     and debtor_name like concat('%', #{debtorName}, '%')
     and apply_user = #{applyUser}
     and apply_time = #{applyTime}
     and approve_user = #{approveUser}
     and approve_statu = #{approveStatu}
     and real_create_by = #{realCreateBy}
     and creator = #{creator}
     and reviser = #{reviser}
     and role_type = #{roleType}

order by update_time desc

SELECT
distinct a.project_name
FROM
sys_apply_in a
LEFT JOIN sys_user b ON a.apply_user = b.login_name
LEFT JOIN sys_user c ON a.real_create_by = c.login_name
LEFT JOIN sys_user d ON a.reviser = d.login_name
LEFT JOIN sys_project_zck ack  on a.project_id = ack.project_zck_id

     and a.apply_type = #{applyType}
     and a.document_type = #{documentType}
     and a.project_id = #{projectId}
     and b.user_name like concat('%', #{applyUserName}, '%')
     and a.project_name like concat('%', #{projectName}, '%')
     and a.company_name like concat('%', #{companyName}, '%')
     and a.debtor_name like concat('%', #{debtorName}, '%')

        and date_format(apply_time,'%Y%m%d') >= date_format(#{params.beginApplyTime},'%Y%m%d')


        and date_format(apply_time,'%Y%m%d') <= date_format(#{params.endApplyTime},'%Y%m%d')

     and (a.apply_user = #{applyUser} or (a.real_create_by = #{applyUser} and a.approve_statu='0'))
     and a.apply_time = #{applyTime}
     and a.approve_user = #{approveUser}
     and a.approve_statu = #{approveStatu}
     and a.creator = #{creator}
     and a.reviser = #{reviser}
     and a.role_type = #{roleType}
     and ack.project_zck_type = #{projectZckType}

order by a.update_time desc

SELECT
distinct a.company_name
FROM
sys_apply_in a
LEFT JOIN sys_user b ON a.apply_user = b.login_name
LEFT JOIN sys_user c ON a.real_create_by = c.login_name
LEFT JOIN sys_user d ON a.reviser = d.login_name
LEFT JOIN sys_project_zck ack  on a.project_id = ack.project_zck_id

     and a.apply_type = #{applyType}
     and a.document_type = #{documentType}
     and a.project_id = #{projectId}
     and b.user_name like concat('%', #{applyUserName}, '%')
     and a.project_name like concat('%', #{projectName}, '%')
     and a.company_name like concat('%', #{companyName}, '%')
     and a.debtor_name like concat('%', #{debtorName}, '%')

        and date_format(apply_time,'%Y%m%d') >= date_format(#{params.beginApplyTime},'%Y%m%d')


        and date_format(apply_time,'%Y%m%d') <= date_format(#{params.endApplyTime},'%Y%m%d')

     and (a.apply_user = #{applyUser} or (a.real_create_by = #{applyUser} and a.approve_statu='0'))
     and a.apply_time = #{applyTime}
     and a.approve_user = #{approveUser}
     and a.approve_statu = #{approveStatu}
     and a.creator = #{creator}
     and a.reviser = #{reviser}
     and a.role_type = #{roleType}
     and ack.project_zck_type = #{projectZckType}

order by a.update_time desc

SELECT app.apply_id, app.apply_type, app.document_type, app.project_id, app.project_name, app.company_name, app.debtor_id, app.debtor_name, app.instance_id, app.apply_user, app.apply_time, app.approve_user, app.role_type, IF ( WORK.approve_statu = 7 || WORK.approve_statu = 8 || WORK.approve_statu = 9, app.approve_statu, WORK.approve_statu ) AS approve_statu, app.real_create_by, app.remarks, app.create_by, app.creator, app.create_time, app.update_by, app.reviser, app.update_time , app.is_return, app.plan_return_time, app.real_return_time, app.out_reason, app.is_out, app.is_receive, app.is_returned, app.is_received, app.remarks_out, ub.user_name as applyUserName, uc.user_name as realCreateName, ud.user_name as reviserName FROM sys_apply_in app INNER JOIN ( SELECT workflow.apply_id, workflow.approve_statu FROM sys_apply_workflow workflow, ( SELECT MAX( a.create_time ) AS create_time, a.apply_id FROM sys_apply_workflow a WHERE a.approve_user = #{applyUser} GROUP BY a.apply_id ORDER BY a.create_time DESC ) b WHERE workflow.create_time = b.create_time AND workflow.apply_id = b.apply_id and workflow.approve_user=#{applyUser} ) WORK ON app.apply_id = WORK.apply_id LEFT JOIN sys_user ub on app.apply_user=ub.login_name LEFT JOIN sys_user uc ON app.real_create_by = uc.login_name LEFT JOIN sys_user ud ON app.reviser = ud.login_name
and app.apply_type = #{applyType} and app.document_type = #{documentType} and app.project_id = #{projectId} and app.project_name like concat('%', #{projectName}, '%') and app.company_name like concat('%', #{companyName}, '%') and ub.user_name like concat('%', #{applyUserName}, '%') and app.debtor_name like concat('%', #{debtorName}, '%') and app.apply_time = #{applyTime} and app.approve_user = #{approveUser} and app.approve_statu = #{approveStatu} and app.real_create_by = #{realCreateBy} and app.creator = #{creator}
and date_format(app.apply_time,'%Y%m%d') >= date_format(#{params.beginApplyTime},'%Y%m%d')
and date_format(app.apply_time,'%Y%m%d') <= date_format(#{params.endApplyTime},'%Y%m%d')
and app.reviser = #{reviser}
ORDER BY app.apply_time desc

mapper.update:
update sys_apply_in
apply_type = #{applyType}, document_type = #{documentType}, project_id = #{projectId}, project_name = #{projectName}, company_name = #{companyName}, debtor_id = #{debtorId}, debtor_name = #{debtorName}, apply_user = #{applyUser}, apply_time = #{applyTime}, approve_user = #{approveUser}, approve_statu = #{approveStatu}, real_create_by = #{realCreateBy}, remarks = #{remarks}, create_by = #{createBy}, creator = #{creator}, create_time = #{createTime}, update_by = #{updateBy}, reviser = #{reviser}, update_time = #{updateTime}, instance_id = #{instanceId}, is_return = #{isReturn}, plan_return_time = #{planReturnTime}, real_return_time = #{realReturnTime}, out_reason = #{outReason}, is_out = #{isOut}, is_receive = #{isReceive}, is_returned = #{isReturned}, is_received = #{isReceived}, remarks_out = #{remarksOut}, role_type = #{roleType},
where apply_id = #{applyId}
