mapper.update:
update sys_customer
customer_name = #{customerName}, affiliation = #{affiliation}, contacts = #{contacts}, contact_number = #{contactNumber}, duties = #{duties}, contact_address = #{contactAddress}, resources = #{resources}, province = #{province}, city = #{city}, county = #{county}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, dept_id = #{deptId}, dept_name = #{deptName}, weChat_flag = #{wechatFlag}, reason = #{reason}, we_chat_number = #{weChatNumber}, creator = #{creator}, reviser = #{reviser}, agent = #{agent}, agent_id = #{agentId}, customer_lable = #{customerLable}, user_id = #{shareUserId}, share_user_name = #{shareUserName}, remarks = #{remarks}, dept_type = #{deptType},
where customer_id = #{customerId}

mapper.select:
select customer_id, contact_number from sys_customer where contact_number like concat('%', #{contactNumber},
'%') and del_flag = '0'

    and customer_id =#{customerId}


    and dept_id in

        #{deptId}

select customer_id, we_chat_number from sys_customer where we_chat_number=#{weChatNumber} and del_flag = '0'

    and dept_id in

        #{deptId}


t1

    t1.del_flag = '0'
    and t1.customer_name like concat('%',
        #{customerName},'%')

    and t1.affiliation = #{affiliation}
    and t1.contacts like concat('%',#{contacts}, '%')
    and t1.contact_number like concat('%',
        #{contactNumber}, '%')

    and t1.duties = #{duties}
    and t1.contact_address = #{contactAddress}
    and t1.resources = #{resources}
    and t1.province = #{province}
    and t1.city = #{city}
    and t1.county = #{county}
    and t1.dept_name like concat('%',#{deptName}, '%')
    and t1.weChat_flag = #{wechatFlag}
    and t1.reason like concat('%', #{reason, '%')
    and t1.create_by = #{createBy}
    and t1.we_chat_number like
        concat('%',#{weChatNumber}, '%')




        AND date_format(t1.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(t1.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

    and t1.creator like concat('%', #{creator}, '%')
    and t1.reviser like concat('%', #{reviser}, '%')
    and t1.dept_id = #{deptId}

        AND t1.dept_id in

            #{deptId}


    and t1.agent like concat('%', #{agent}, '%')
    and t1.customer_lable like concat('%',
        #{customerLable}, '%')


        AND create_by like concat('%', #{createBy}, '%')


GROUP BY customer_id

SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(t1.dept_name,',',t2. help_topic_id+1),',',-1) AS dept_name ,count(*) AS customerLableCount,GROUP_CONCAT(customer_lable) AS customerLable,dept_type FROM lxztest.sys_customer t1 JOIN mysql.help_topic t2 on t2. help_topic_id < LENGTH(t1.dept_name)-LENGTH(REPLACE(t1.dept_name,',',''))+1 WHERE t1.del_flag='0'
AND date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
and dept_type =#{deptType}
GROUP BY dept_name ORDER BY customerLableCount desc;
