select.where:
del_flag = '0'
and customer_name like concat('%', #{customerName},
    '%')

and contacts = #{contacts}
and contact_number = #{contactNumber}
and province = #{province}
and city = #{city}
and county = #{county}

    AND dept_id in

        #{deptId}


and dept_name like concat('%',#{deptName}, '%')
and weChat_flag = #{wechatFlag}
and reason = #{reason}
and create_by = #{createBy}
and we_chat_number = #{weChatNumber}

    AND date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

and creator like concat('%', #{creator}, '%')
and reviser like concat('%', #{reviser}, '%')
and agent like concat('%', #{agent}, '%')
and customer_lable = #{customerLable}

mapper.update:
update sys_customer
customer_name = #{customerName}, affiliation = #{affiliation}, contacts = #{contacts}, contact_number = #{contactNumber}, duties = #{duties}, contact_address = #{contactAddress}, resources = #{resources}, province = #{province}, city = #{city}, county = #{county}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, dept_id = #{deptId}, dept_name = #{deptName}, weChat_flag = #{wechatFlag}, reason = #{reason}, we_chat_number = #{weChatNumber}, creator = #{creator}, reviser = #{reviser}, agent = #{agent}, agent_id = #{agentId}, customer_lable = #{customerLable},
where customer_id = #{customerId}

mapper.select:
SELECT t1.customer_id, t1.customer_name, t1.affiliation, t1.contacts, t1.contact_number, t1.duties,
t1.contact_address, t1.resources, t1.province, t1.city, t1.county, t1.del_flag, t1.create_by, t1.create_time,
t1.update_by, t1.update_time, t1.dept_id, t1.dept_name,
t1.weChat_flag,t1.reason, t1.we_chat_number,t1.creator,t1.reviser,t1.agent_id,t1.agent,t1.customer_lable FROM
sys_customer t1

    t1.del_flag = '0'
    and t1.customer_name like concat('%',
        #{customerName},'%')

    and t1.affiliation = #{affiliation}
    and t1.contacts like concat('%',#{contacts}, '%')
    and t1.contact_number like concat('%',
        #{contactNumber}, '%')

    and t1.duties = #{duties}
    and t1.contact_address = #{contactAddress}
    and t1.resources = #{resources}
    and t1.province = #{province}
    and t1.city = #{city}
    and t1.county = #{county}
    and t1.dept_name like concat('%',#{deptName}, '%')
    and t1.weChat_flag = #{wechatFlag}
    and t1.reason like concat('%', #{reason, '%')
    and t1.create_by = #{createBy}
    and t1.we_chat_number like
        concat('%',#{weChatNumber}, '%')




        AND date_format(t1.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(t1.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

    and t1.creator like concat('%', #{creator}, '%')
    and t1.reviser like concat('%', #{reviser}, '%')
    and t1.dept_id = #{deptId}

        AND t1.dept_id in

            #{deptId}


    and t1.agent like concat('%', #{agent}, '%')
    and t1.customer_lable like concat('%',
        #{customerLable}, '%')


GROUP BY customer_id
