mapper.select:


    del_flag = '0'
    and project_name like concat ('%', #{projectName},'%')
    and asset_status = #{assetStatus}
    and no = #{no}
    and borrower = #{borrower}
    and city = #{city}
    and loan_bank = #{loanBank}
    and contract_principal = #{contractPrincipal}
    and principal_balance = #{principalBalance}
    and interest_balance = #{interestBalance}
    and principal_interest_balance = #{principalInterestBalance}

    and guarantor = #{guarantor}
    and pledge = #{pledge}
    and collateral = #{collateral}
    and project_manager_id = #{projectManagerId}

    and project_manager = #{projectManager}
    and user_id = #{userId}
    and judicial_status = #{judicialStatus}
    and competent_court = #{competentCourt}
    and judge_contact = #{judgeContact}
    and judicial_remarks = #{judicialRemarks}
    and collateral_claims =
        #{collateralClaims}

    and transaction_type = #{transactionType}
    and buyer = #{buyer}
    and buyer_id = #{buyerId}
    and buyer_sources = #{buyerSources}
    and buyer_contact = #{buyerContact}
    and contract_no = #{contractNo}
    and limitation_action = #{limitationAction}
    and seal_up_subject_matter =
        #{sealUpSubjectMatter}

    and seal_up_date = #{sealUpDate}
    and limitation_execution = #{limitationExecution}
    and date_filing = #{dateFiling}
    and first_instance = #{firstInstance}
    and second_instance = #{secondInstance}
    and retrial = #{retrial}
    and enforcement_filing_date = #{enforcementFilingDate}
    and appraisal_report_date = #{appraisalReportDate}
    and first_shot_date = #{firstShotDate}
    and second_shot_date = #{secondShotDate}
    and sell_shot_date = #{sellShotDate}
    and successful_bidder_date = #{successfulBidderDate}
    and payment_received_date = #{paymentReceivedDate}
    and parent_id = #{parentId}
    and project_zck_id = #{projectZckId}
    and lawyer_contact = #{lawyerContact}
    and law_firm = #{lawFirm}
     and debt_status = #{debtStatus}

ORDER BY no asc

