select.where:
del_flag='0'
and asset_package_name = #{assetPackageName} 
and asset_status = #{assetStatus}
and team_members_id like
    concat('%',#{teamMembersId},'%')

mapper.update:
update sys_zcb
asset_package_name = #{assetPackageName}, asset_status = #{assetStatus}, team_members_id = #{teamMembersId}, team_members_name = #{teamMembersName}, update_by = #{updateBy}, update_time = #{updateTime}, evaluation_time = #{evaluationTime},
where id = #{id}
