    and soe = #{soe}
    and operation = #{operation}
    and court = #{court}
    and litigation = #{litigation}
    and judicial_remark = #{judicialRemark}
    and dyw_address = #{dywAddress}
    and mortgage_amount = #{mortgageAmount}
    and mortgage_contract_no =
        #{mortgageContractNo}

    and
        maximum_mortgage_contract_no = #{maximumMortgageContractNo}

    and zge_contract_time = #{zgeContractTime}
    and dbzzq_time = #{dbzzqTime}
    and mortgagor = #{mortgagor}
    and zge_qs_no = #{zgeQsNo}
    and tx_qz_no = #{txQzNo}
    and case_no = #{caseNo}
    and mortgage_rank like concat
        ('%',#{mortgageRank },'%')

    and qxsw_mortgeage_amount = #{qxswMortgeageAmount}
    and before_or_ater_aarrival =
        #{beforeOrAterAarrival}

    and rental_details = #{rentalDetails}
    and juveniles = #{juveniles}
    and only_house = #{onlyHouse}
    and tengfang = #{tengfang}
