    and price = #{price}
    and desposal_mode = #{desposalMode}
    and desposal_price = #{desposalPrice}
    and customer_id = #{customerId}
    and customer = #{customer}
    and dzywqd_time = #{dzywqdTime}
    and maximum_mortgage_amount = #{maximumMortgageAmount}
    and rental_situation = #{rentalSituation}
    and zoning = #{zoning}
    and zcb_id=#{zcbId}
    and parent_id=#{parentId}

ORDER BY no

